import Transform from './Transform';
import ClickBoxUI from './ClickBoxUI';

class CoordinateSystemUI {

    constructor (shader, axisLength, axisWidth) {

        this.gl = shader.gl;
        this.positionAttributeLocation = shader.positionAttributeLocation;
        this.normalAttributeLocation = shader.normalAttributeLocation;
        this.colorUniformLocation = shader.colorUniformLocation;
        this.matrixUniformLocation = shader.worldViewUniformLocation;

        // Create one cube model for all three axes
        this.CreateAxis();

        let triangles = 12;
        this.totalVertices = triangles*3;

        // Size dimensions and positioning for the axes
        this.xAxisTransform = new Transform();
        this.xAxisTransform.setPosition([axisLength+axisWidth, 0.0, 0.0]);
        this.xAxisTransform.setScale([axisLength, axisWidth, axisWidth]);
        this.yAxisTransform = new Transform();
        this.yAxisTransform.setPosition([0.0, axisLength-axisWidth, 0.0]);
        this.yAxisTransform.setScale([axisWidth, axisLength, axisWidth]);
        this.zAxisTransform = new Transform();
        this.zAxisTransform.setPosition([0.0, 0.0, axisLength+axisWidth]);
        this.zAxisTransform.setScale([axisWidth, axisWidth, axisLength]);

        let clickBoxVertices = [
            // V0
            [-1.0, -1.0, -1.0, 1.0],
            // V1
            [-1.0, -1.0, 1.0, 1.0],
            // V2
            [1.0, -1.0, 1.0, 1.0],
            // V3
            [1.0, -1.0, -1.0, 1.0],
            // V4
            [-1.0, 1.0, -1.0, 1.0],
            // V5
            [-1.0, 1.0, 1.0, 1.0],
            // V6
            [1.0, 1.0, 1.0, 1.0],
            // V7
            [1.0, 1.0, -1.0, 1.0]
        ];

        this.clickBoxAxisX = new ClickBoxUI(clickBoxVertices);
        this.clickBoxAxisY = new ClickBoxUI(clickBoxVertices);
        this.clickBoxAxisZ = new ClickBoxUI(clickBoxVertices);

        this.UpdateClickBox();

        this.isVisible = false;
    }

    UpdateClickBox() {
        this.clickBoxAxisX.UpdateBorders(this.xAxisTransform.matrix);
        this.clickBoxAxisY.UpdateBorders(this.yAxisTransform.matrix);
        this.clickBoxAxisZ.UpdateBorders(this.zAxisTransform.matrix);
    }

    SetParentTransform(parentTransform) {

        // Ignore scaling
        let temp = parentTransform.Clone();
        // Reset to default scale
        temp.SetScale([1.0, 1.0, 1.0]);

        this.xAxisTransform.setParent(temp);
        this.yAxisTransform.setParent(temp);
        this.zAxisTransform.setParent(temp);
        this.UpdateClickBox();
    }

    SetVisible(flag) {
        this.isVisible = flag;
    }

    BindBuffersAndEnableAttributes() {

        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.vertexBuffer);
        this.gl.vertexAttribPointer(
            this.positionAttributeLocation, // Attribute location
            3, // Number of elements per iteration (x,y,z)
            this.gl.FLOAT, // Type of elements (32bit floats)
            this.gl.FALSE, // Don't normalize the data
            6 * Float32Array.BYTES_PER_ELEMENT, // how many bytes to move to the next vertex
            0// Offset from the beginning of a single vertex to this attribute
        );
        this.gl.vertexAttribPointer(
            this.normalAttributeLocation, // Attribute location
            3, // Number of elements per iteration (x,y,z)
            this.gl.FLOAT, // Type of elements (32bit floats)
            this.gl.TRUE, // Not normalized
            6 * Float32Array.BYTES_PER_ELEMENT, // how many bytes to move to the next normal
            3 * Float32Array.BYTES_PER_ELEMENT// Offset from the beginning of a single vertex to this attribute
        );
        this.gl.enableVertexAttribArray(this.positionAttributeLocation);
        this.gl.enableVertexAttribArray(this.normalAttributeLocation);
        this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
    }

    Render() {

        if (this.isVisible) {

            this.BindBuffersAndEnableAttributes();

            // X-Axis
            this.gl.uniformMatrix4fv(this.matrixUniformLocation, this.gl.FALSE, this.xAxisTransform.matrix);
            this.gl.uniform3fv(this.colorUniformLocation, [1.0, 0.0, 0.0]);
            this.gl.drawElements(this.gl.TRIANGLES, this.totalVertices, this.gl.UNSIGNED_SHORT, 0);

            // Y-Axis
            this.gl.uniformMatrix4fv(this.matrixUniformLocation, this.gl.FALSE, this.yAxisTransform.matrix);
            this.gl.uniform3fv(this.colorUniformLocation, [0.0, 1.0, 0.0]);
            this.gl.drawElements(this.gl.TRIANGLES, this.totalVertices, this.gl.UNSIGNED_SHORT, 0);

            // Z-Axis
            this.gl.uniformMatrix4fv(this.matrixUniformLocation, this.gl.FALSE, this.zAxisTransform.matrix);
            this.gl.uniform3fv(this.colorUniformLocation, [0.0, 0.0, 1.0]);
            this.gl.drawElements(this.gl.TRIANGLES, this.totalVertices, this.gl.UNSIGNED_SHORT, 0);

            // Unbind
            this.gl.bindBuffer(this.gl.ARRAY_BUFFER, null);
            this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, null);
        }
    }

    CreateAxis () {
        // Create a cube
        let x = 1.0;
        let y = 1.0;
        let z = 1.0;

        // Vertex coordinates, normals and texture coordinates
        let vertexArray = [	// X, Y, Z      Normal
            // Vertex 0
            -x, -y, -z,      0.0, -1.0,  0.0,
            -x, -y, -z,     -1.0,  0.0,  0.0,
            -x, -y, -z,      0.0,  0.0,  -1.0,

            // Vertex 1
            -x, -y,  z,     0.0, -1.0,  0.0,
            -x, -y,  z,     0.0,  0.0, 1.0,
            -x, -y,  z,    -1.0,  0.0,  0.0,

            // Vertex 2
            x, -y,  z,      0.0, -1.0,  0.0,
            x, -y,  z,      0.0,  0.0, 1.0,
            x, -y,  z,      1.0,  0.0,  0.0,

            // Vertex 3
            x, -y, -z,      0.0, -1.0,  0.0,
            x, -y, -z,      0.0,  0.0,  -1.0,
            x, -y, -z,      1.0,  0.0,  0.0,

            // Vertex 4
            -x, y, -z,     -1.0,  0.0,  0.0,
            -x, y, -z,      0.0,  0.0,  -1.0,
            -x, y, -z,      0.0,  1.0,  0.0,

            // Vertex 5
            -x, y, z,      0.0,  0.0, 1.0,
            -x, y, z,     -1.0,  0.0,  0.0,
            -x, y, z,      0.0,  1.0,  0.0,

            // Vertex 6
            x, y, z,       0.0,  0.0, 1.0,
            x, y, z,       1.0,  0.0,  0.0,
            x, y, z,       0.0,  1.0,  0.0,

            // Vertex 7
            x, y, -z,       0.0,  0.0,  -1.0,
            x, y, -z,       1.0,  0.0,  0.0,
            x, y, -z,       0.0,  1.0,  0.0
        ];

        // Vertex indices
        let indexArray = [	// Bottom
            0, 9, 6,  // v0, v3, v2
            6, 3, 0,  // v2, v1, v0

            // Front
            4, 7, 18,  // v1, v2, v6
            18, 15, 4, // v6, v5, v1

            // Left
            1, 5, 16,  // v0, v1, v5
            16, 12, 1, // v5, v4, v0

            // Back
            2, 13, 21, // v0, v4, v7
            21, 10, 2, // v7, v3, v0

            // Right
            8, 11, 22, // v2, v3, v7
            22, 19, 8, // v7, v6, v2

            // Top
            14, 17, 20, // v4, v5, v6
            20, 23, 14  // v6, v7, v4
        ];

        // Create vertex buffer
        this.vertexBuffer = this.gl.createBuffer();
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.vertexBuffer);
        this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(vertexArray), this.gl.STATIC_DRAW);

        // Create index buffer
        this.indexBuffer = this.gl.createBuffer();
        this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
        this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(indexArray), this.gl.STATIC_DRAW);

        /*** SHADER STUFF ***/
        this.gl.vertexAttribPointer(
            this.positionAttributeLocation, // Attribute location
            3, // Number of elements per iteration (x,y,z)
            this.gl.FLOAT, // Type of elements (32bit floats)
            this.gl.FALSE, // Don't normalize the data
            6 * Float32Array.BYTES_PER_ELEMENT, // how many bytes to move to the next vertex
            0// Offset from the beginning of a single vertex to this attribute
        );
        this.gl.vertexAttribPointer(
            this.normalAttributeLocation, // Attribute location
            3, // Number of elements per iteration (x,y,z)
            this.gl.FLOAT, // Type of elements (32bit floats)
            this.gl.TRUE, // Not normalized
            6 * Float32Array.BYTES_PER_ELEMENT, // how many bytes to move to the next normal
            3 * Float32Array.BYTES_PER_ELEMENT// Offset from the beginning of a single vertex to this attribute
        );
        this.gl.enableVertexAttribArray(this.positionAttributeLocation);
        this.gl.enableVertexAttribArray(this.normalAttributeLocation);
    }
}

export default CoordinateSystemUI;
