import Matrix4 from './math/Matrix4';

class IntersectionField {
    constructor() {
        this.vertices = [
            // V0
            [-1.0, -1.0, -1.0, 1.0],
            // V1
            [-1.0, -1.0, 1.0, 1.0],
            // V2
            [1.0, -1.0, 1.0, 1.0],
            // V3
            [1.0, -1.0, -1.0, 1.0],
            // V4
            [-1.0, 1.0, -1.0, 1.0],
            // V5
            [-1.0, 1.0, 1.0, 1.0],
            // V6
            [1.0, 1.0, 1.0, 1.0],
            // V7
            [1.0, 1.0, -1.0, 1.0]
        ];

        this.borders = {
            xMin: 0,
            xMax: 0,
            yMin: 0,
            yMax: 0
        };
    }

    static setCanvasDimensions(width, height) {
        this.canvasWidth = width;
        this.canvasHeight = height;
    }

    updateBorders(camera, matrix, width, height) {
        this.canvasWidth = width;
        this.canvasHeight = height;

        if (camera) {
            this.borders.xMin = width+1;
            this.borders.xMax = -1;
            this.borders.yMin = height+1;
            this.borders.yMax = -1;

            // Set the width and height of the viewport
            let screenW = width/(width*Math.sqrt(width/height));
            //let screenW = 1.0;
            let screenH = height/(width*Math.sqrt(width/height));
            //let screenH = height/width;

            for (let i = 0; i < this.vertices.length; ++i) {
                // Take one point from a local coordinate system
                let screenCoordinates = this.vertices[i];
                // Transform the local coordinate system point to the world coordinate system
                screenCoordinates = Matrix4.multiplyVector(matrix, screenCoordinates);
                // Transform this point to the cameras local coordinate system
                screenCoordinates = Matrix4.multiplyVector(camera.transform.matrix, screenCoordinates);

                // Compute the coordinates from camera space onto the canvas by using perspective projection
                // px = x/(-z), py = y/(-z)
                let px = (screenCoordinates[0]/(-screenCoordinates[2]));
                let py = (screenCoordinates[1]/(-screenCoordinates[2]));

                // Convert coordinates from screen space to raster space (Normalized Device Coordinates)
                let pxNormalized = (px+(width/2)) / width;
                let pyNormalized = (py+(height/2)) / height;

                // FROM PIXEL COORDINATES TO NDC  COORDINATES
                //let pxNormalized = 2.0 * (px + 0.5) / width - 1.0;
                //let pyNormalized = 2.0 * (py + 0.5) / height - 1.0;

                // Covert to pixel coordinates (Raster space)
                let pxRaster = Math.floor(pxNormalized * width);
                let pyRaster = Math.floor((1-pyNormalized) * height);

                if (pxRaster < 0)
                    pxRaster = 0;
                else if (pxRaster > width)
                    pxRaster = width;
                if (pyRaster < 0)
                    pyRaster = 0;
                else if (pyRaster > height)
                    pyRaster = height;

                if (pxRaster < this.borders.xMin) {
                    this.borders.xMin = pxRaster;
                }
                else if (pxRaster > this.borders.xMax) {
                    this.borders.xMax = pxRaster;
                }

                if (pyRaster < this.borders.yMin) {
                    this.borders.yMin = pyRaster;
                }
                else if (pyRaster > this.borders.yMax) {
                    this.borders.yMax = pyRaster;
                }
            }
        }
    }
}

export default IntersectionField;
